#include<stdio.h>
/*typecasting syntax
(type) value*/
int main()
{
    float a=54/5;
    float b=(float)54/5;
    printf("Value of 54/5 without typecasting is %f\n",a);
    printf("Value of 54/5 with typecasting is %f",b) ;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
    return 0;
}
